    img: "/images/article79_image1.jpg",
    img_source: "https://www.amazon.com/Veniss-Underground-Novel-Jeff-VanderMeer/dp/1250860954",
    img_source_title: "Amazon.com",
    author: "James Murray",
    topics: ["ARTS & CULTURE", "BOOKS"],
